import React from 'react';
import './ItemsAddForm.css';

class ItemsAddForm extends React.Component {

    state = {
        text: ''
    }

    addItem(e) {
        e.preventDefault();
        this.props.onAddItem();
    }

    render() {
        return(
            <form className = 'add-form' onSubmit = {this.addItem.bind(this)}>
                <input type = 'text' placeholder = 'What do you need to do?' value = {this.state.value} />
                <button className = 'add-button'>Add Item</button>
            </form>
        );
    }
}

export default ItemsAddForm;
