import React from 'react';
import './TodoListItem.css';

function TodoListItem({ title, onRemoveItem, onImportantSet, onDoneSet }) {
    return(
        <li>
            <div onClick = {onDoneSet}>
                {title}
            </div>
            <div>
                <button className = 'icon' onClick = {onImportantSet}>
                    <i className = 'fa fa-exclamation fa-2x'></i>
                </button>
                <button className = 'icon' onClick = {onRemoveItem}>
                    <i className = 'fa fa-trash-o fa-2x'></i>
                </button>
            </div>
        </li>
    )
}

export default TodoListItem;
